CREATE TABLE pessoa(
    codigo BIGINT(20) PRIMARY KEY AUTO_INCREMENT,
    nome VARCHAR(50) NOT NULL,
    ativo TINYINT(1) NOT NULL,
	logradouro  VARCHAR(100),
	numero  VARCHAR(12),
	complemento  VARCHAR(100),
	bairro VARCHAR(100),
	cep CHAR(9),
	cidade VARCHAR(100),
	estado CHAR(2)
)ENGINE=InnoDB DEFAULT CHARSET=utf8;

